import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import HealthcareRecords from "./contracts/HealthcareRecords.json";

const CONTRACT_ADDRESS = "YOUR_DEPLOYED_CONTRACT_ADDRESS";

function Healthcare() {
  const [contract, setContract] = useState(null);
  const [account, setAccount] = useState(null);
  const [isOwner, setIsOwner] = useState(false);

  const [patientID, setPatientID] = useState("");
  const [patientName, setPatientName] = useState("");
  const [gender, setGender] = useState("");
  const [age, setAge] = useState("");
  const [bloodGroup, setBloodGroup] = useState("");
  const [diagnosis, setDiagnosis] = useState("");
  const [treatment, setTreatment] = useState("");
  const [doctor, setDoctor] = useState("");
  const [providerAddress, setProviderAddress] = useState("");

  const [patientRecords, setPatientRecords] = useState([]);

  // connect wallet + contract
  useEffect(() => {
    const loadContract = async () => {
      if (window.ethereum) {
        //const provider = new ethers.providers.Web3Provider(window.ethereum);
        const provider = new ethers.providers.Web3Provider(window.ethereum, "any");
        const signer = provider.getSigner();
        const healthcareContract = new ethers.Contract(
          CONTRACT_ADDRESS,
          HealthcareRecords.abi,
          signer
        );
        setContract(healthcareContract);

        const accounts = await window.ethereum.request({
          method: "eth_requestAccounts",
        });
        setAccount(accounts[0]);

        // check owner
        const owner = await healthcareContract.getOwner();
        setIsOwner(owner.toLowerCase() === accounts[0].toLowerCase());
      }
    };
    loadContract();
  }, []);

  const addRecord = async () => {
    try {
      if (!patientID || !patientName || !diagnosis || !treatment || !doctor) {
        alert("Please fill all fields");
        return;
      }

      const tx = await contract.addRecord(
        patientID,
        patientName,
        diagnosis,
        treatment
      );
      await tx.wait();

      alert("Patient record added successfully");

      // reset inputs
      setPatientName("");
      setGender("");
      setAge("");
      setBloodGroup("");
      setDiagnosis("");
      setTreatment("");
      setDoctor("");
      setPatientID("");
    } catch (error) {
      console.error("Error adding record:", error);
    }
  };

  const fetchRecords = async () => {
    try {
      const records = await contract.getPatientRecords(patientID);
      setPatientRecords(records);
    } catch (error) {
      console.error("Error fetching records:", error);
    }
  };

  const authorizeProvider = async () => {
    try {
      const tx = await contract.authorizeProvider(providerAddress);
      await tx.wait();
      alert("Provider authorized successfully");
      setProviderAddress("");
    } catch (error) {
      console.error("Error authorizing provider:", error);
    }
  };

  const authorizeMyAccount = async () => {
    try {
      const tx = await contract.authorizeProvider(account);
      await tx.wait();
      alert(`Your account ${account} has been authorized`);
    } catch (error) {
      console.error("Error authorizing yourself:", error);
    }
  };

  return (
    <div style={{ maxWidth: "800px", margin: "0 auto", padding: "20px" }}>
      <h1 style={{ textAlign: "center", marginBottom: "20px" }}>
        Healthcare Records DApp
      </h1>

      {account && (
        <p style={{ textAlign: "center" }}>
          <b>Connected Account:</b> {account}
        </p>
      )}
      {isOwner && (
        <p style={{ textAlign: "center", color: "green" }}>
          âœ… You are the contract owner
        </p>
      )}

      {/* Fetch Records */}
      <div style={boxStyle}>
        <h2>Fetch Patient Records</h2>
        <input
          type="text"
          placeholder="Patient ID"
          value={patientID}
          onChange={(e) => setPatientID(e.target.value)}
        />
        <button style={btnStyle} onClick={fetchRecords}>
          Fetch Records
        </button>
      </div>

      {/* Add Record */}
      <div style={boxStyle}>
        <h2>Add Patient Record</h2>
        <input
          type="text"
          placeholder="Patient Name"
          value={patientName}
          onChange={(e) => setPatientName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Gender"
          value={gender}
          onChange={(e) => setGender(e.target.value)}
        />
        <input
          type="number"
          placeholder="Age"
          value={age}
          onChange={(e) => setAge(e.target.value)}
        />
        <input
          type="text"
          placeholder="Blood Group"
          value={bloodGroup}
          onChange={(e) => setBloodGroup(e.target.value)}
        />
        <input
          type="text"
          placeholder="Diagnosis"
          value={diagnosis}
          onChange={(e) => setDiagnosis(e.target.value)}
        />
        <input
          type="text"
          placeholder="Treatment"
          value={treatment}
          onChange={(e) => setTreatment(e.target.value)}
        />
        <input
          type="text"
          placeholder="Doctor Name"
          value={doctor}
          onChange={(e) => setDoctor(e.target.value)}
        />
        <button style={btnStyle} onClick={addRecord}>
          Add Record
        </button>
      </div>

      {/* Authorize Provider */}
      <div style={boxStyle}>
        <h2>Authorize HealthCare Provider</h2>
        <input
          type="text"
          placeholder="Provider Address"
          value={providerAddress}
          onChange={(e) => setProviderAddress(e.target.value)}
        />
        <button style={btnStyle} onClick={authorizeProvider}>
          Authorize Provider
        </button>
      </div>

      {/* Authorize Yourself */}
      <div style={boxStyle}>
        <h2>Authorize Yourself</h2>
        <button style={btnStyle} onClick={authorizeMyAccount}>
          Authorize My Account
        </button>
      </div>

      {/* Display Records */}
      <div style={boxStyle}>
        <h2>Patient Records</h2>
        {patientRecords.length === 0 ? (
          <p>No records found</p>
        ) : (
          patientRecords.map((record, idx) => (
            <div key={idx} style={recordCard}>
              <p>
                <b>Record ID:</b> {record.recordID.toString()}
              </p>
              <p>
                <b>Patient Name:</b> {record.patientName}
              </p>
              <p>
                <b>Diagnosis:</b> {record.diagnosis}
              </p>
              <p>
                <b>Treatment:</b> {record.treatment}
              </p>
              <p>
                <b>Timestamp:</b>{" "}
                {new Date(record.timestamp * 1000).toLocaleString()}
              </p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

const boxStyle = {
  background: "#f9f9f9",
  padding: "15px",
  marginBottom: "20px",
  borderRadius: "8px",
  border: "1px solid #ddd",
};

const btnStyle = {
  background: "green",
  color: "white",
  padding: "8px 15px",
  margin: "5px",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
};

const recordCard = {
  background: "white",
  padding: "10px",
  border: "1px solid #ccc",
  borderRadius: "8px",
  marginBottom: "10px",
};

export default Healthcare;
